dt-example.js:
$(document).ready(function() {
	$('#example').DataTable( {
		ajax:        "data/2500.txt",
		deferRender: true,
		dom:         "frtiS",
		scrollY:     200,
		scrollCollapse: true,
		initComplete: function () {
			var api = this.api();
			api.scroller().scrollToRow( 1000 );
		}
	} );
} );
